void solve() {
    lng l, r;
    cin >> l >> r;

    lng ans = 0;
    if (r >= 100) 
        ans += r - max(l, 100ll) + 1;

    for (lng b : banned)
        if (l <= b && b <= r)
            ans--;
    
    cout << ans << endl;

    return;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);

    int t = 1;
    cin >> t;
    while (t--) {
        solve();
    }

    return 0;
}